function isAgeDiverse(list) {
    console.log(list);

    //   let find = list.find(dev => dev.age >= 0 && dev.age <= 10);
    //   if (!find) return true;

    for (let i = 1; i <= 9; i++) {
        const find = list.find(dev => dev.age.toString()[0] === i.toString());
        if (!find) return false;
    }

    find = list.find(dev => dev.age >= 100);
    if (!find) return false;

    return true;
}

